import React from "react";
import s from "./firstScreen.module.css";
import SecondScreen from '../secondScreen/secondScreen';
import {Route, Routes} from "react-router-dom";
import {Link} from "react-router-dom";

export default function firstScreen(props) {
    
    return (
        <div className={s.container}>
            <div className={s.header}>
                <p >CHOOSE THE COURSE</p>
                <div className={s.buttons}>
                    <Link to="/secondScreen"><button className={s.button}>EURUSD</button></Link>
                    <button className={s.button}>GOLD</button>
                    <button className={s.button}>GOLD</button>
                    <button className={s.button}>GOLD</button>
                </div>
            </div>
            <div className={s.body}>
                <Routes>
                    <Route path="/secondScreen" element={<SecondScreen/>} />
                </Routes>
            </div>
        </div>
    )
}
