import React from "react";
import s from "./secondScreen.module.css";
import { useEffect, useState, useMemo } from "react";
import Chart from 'react-apexcharts';

export default function SecondScreen(props) {

    const [series, setSeries] = useState([{
        data: []
    }])
    const [price, setPrice] = useState(0)
    const [prevPrice, setPrevPrice] = useState(0)
    const [priceTime, setPriceTime] = useState(null)

    const proxyUrl = "https://cors-anywhere.herokuapp.com/"
    const stocksUrl = `${proxyUrl}https://query1.finance.yahoo.com/v8/finance/chart/GME`;

    const chart = {
        options: {
            chart: {
                type: 'candlestick',
                height: 350
            },
            title: {
                text: 'CandleStick Chart',
                align: 'left'
            },
            xaxis: {
                type: 'datetime'
            },
            yaxis: {
                tooltip: {
                    enabled: true
                }
            }
        },
    }


    async function getStocks() {
        const response = await fetch(stocksUrl);
        return response.json();

    }

    const directionEmojis = {
        'up': '',
        'down': '',
        '': ''
    }




    //gme - Game Stop Crop
    useEffect(() => {

        let timeoutId;

        async function getLatestPrice() {

            try {
                const data = await getStocks();
                const gme = data.chart.result[0];
                setPrevPrice(price);
                setPrice(gme.meta.regularMarketPrice.toFixed(2));
                setPriceTime(new Date(gme.meta.regularMarketTime * 1000));
                const quote = gme.indicators.quote[0]
                const prices = gme.chart.timestamp.map((timestamp, index) => {
                    
                    return {
                        x: new Date(timestamp * 1000),
                        // O, H, L, C
                        y: [quote.open[index], quote.high[index], quote.low[index], quote.close[index]]
                    }
            })

            } catch (error) {
                console.log(error);
            }

            timeoutId = setTimeout(getLatestPrice, 10000);
        }
        getLatestPrice();
    }, [])

    const direction = useMemo(() => prevPrice < price ? 'up' : prevPrice > price ? 'down' : '', [prevPrice, price]);





    return (


        <div className={s.container}>
            <p>EURUSD</p>
            <div className={s.buttons}>
                <div className={s.buttonsBuySell}>
                    <button className={s.button}>BUY 0.09974</button>
                    <button className={s.button}>SELL 0.90074</button>
                </div>
                <div>
                    <button className={s.button}>hide chart</button>
                </div>
            </div>
            <div className={s.chart}>
            </div>
            <div className={['price', direction].join(' ')}>
                <div className='price'>
                    ${price}{directionEmojis[direction]}
                </div>
                <div className='priceTime'>
                    {priceTime && priceTime.toLocaleTimeString()}
                </div>
                <Chart options={chart.options} series={series} type="candlestick" width={500} height={320} />
            </div>
        </div>
    )
}
