import React from "react";
import s from "./Layout.module.css";
import CurrencyBlock from '../CurrencyBlock/CurrencyBlock.component';
import {Route, Routes} from "react-router-dom";
import {Link} from "react-router-dom";
import { useLocation } from "react-router-dom";

export default function Layout() {

    const location = useLocation();
    const labell =location.state?.label;
    
    return (
        <div className={s.root}>
            <p>
                {labell?(
                    <p>jest</p>
                ):(
                    <p>nie</p>
                )}
            </p>
            <div className={s.header}>
                <p >CHOOSE THE COURSE</p>
                <div className={s.buttons}>
                    <Link 
                    to={{
                        pathname: "/CurrencyBlock.component",
                        state:{
                            label: "EURUSD",
                        },
                        }} >
                        <button className={s.button}>EURUSD</button>
                    </Link>
                    <button className={s.button}>GOLD</button>
                    <button className={s.button}>GOLD</button>
                    <button className={s.button}>GOLD</button>
                </div>
            </div>
            <div className={s.body}>
                <Routes>
                    <Route path="/CurrencyBlock.component" element={<CurrencyBlock/>} />
                </Routes>
            </div>
        </div>
    )
}
